export const initialState = {
  cart: [
    {
      id: "123125",
      title: "Samsung Galaxy S23 5G (Green, 8GB, 256GB Storage)",
      price: 79999,
      rating: 5,
      image: "https://m.media-amazon.com/images/I/61RZDb2mQxL._SL1500_.jpg",
    },
  ],
  user: null,
};

const reducer = (state, action) => {
  console.log(action);
  switch (action.type) {
    case "ADD_TO_CART":
      // Logic for adding item to cart
      return {
        ...state,
        cart: [...state.cart, action.item],
      };
    case "REMOVE_FROM_CART":
      //Logic for removing item from cart

      //   cloned the cart
      let newCart = [...state.cart];

      const index = state.cart.findIndex(
        (cartItem) => cartItem.id === action.id
      );

      if (index >= 0) {
        //item consist in cart... remove it!
        newCart.splice(index, 1);
      } else {
        console.warn(
          `Can't remove product (id: ${action.id}) as its not in the cart`
        );
      }

      return { ...state, cart: newCart };
    default:
      return state;
  }
};

export default reducer;
